import fs from 'fs';
import path from 'path';
import matter from 'gray-matter';

const postsDirectory = path.join(process.cwd(), 'posts');
const getFileNames = () => fs.readdirSync(postsDirectory);
const getIdFromFileName = fileName => fileName.replace(/\.md$/, '');
const getFullPath = fileName => path.join(postsDirectory, fileName);
const getFileContents = filePath => fs.readFileSync(filePath, 'utf-8');

export function getSortedPostsData(){
  const fileNames = fs.readdirSync(postsDirectory);
  const allPostsData = fileNames.map(fileName => {
    const id = getIdFromFileName(fileName);
    const fullPath = getFullPath(fileName);
    const fileContents = getFileContents(fullPath);

    const matterResult = matter(fileContents);

    return {
      id,
      ...matterResult.data
    };
  });

  return allPostsData.sort((a, b) => a.date < b.date ? 1 : -1);
}

export function getAllPostIds(){
  const fileNames = getFileNames();
  return fileNames.map(fileName => ({
    params: {
      id: getIdFromFileName(fileName)
    }
  }));
}

export function getPostData(id){
  const fullPath = getFullPath(`${id}.md`);
  const fileContents = getFileContents(fullPath);
  const matterResult = matter(fileContents);

  return {id, ...matterResult.data};
}
