import Head from 'next/head';
import Link from 'next/link';
import styles from './Layout.module.css';
import utilStyles from '../styles/utils.module.css';

const name = 'Richard Hwang';
export const siteTitle = 'Next.js Sample Website';

export default function Layout({children, home}){
  return (
    <div className={styles.container}>
      <Head>
        <link rel="icon" href="/favicon.ico" />
        <meta 
          name="description"
          content="Lear how to build a personal website using Next.js"
        />
        <meta 
          property="og:image"
          content={`https://og-image.now.sh/${encodeURI(siteTitle)}.png?theme=light&md=0&fontSize=75px&images=https%3A%2F%2Fassets.vercel.com%2Fimage%2Fupload%2Ffront%2Fassets%2Fdesign%2Fnextjs-black-logo.svg`}
        />
        <meta name="og:title" content={siteTitle} />
        <meta name="twitter:card" content="summary_large_image" />
      </Head>
      <header className={styles.header}>
        {home
        ? (
          <>
            <img src="/images/profile.jpg" 
              className={`${styles.headerHomeImage} ${utilStyles.borderCircle}`}
              alt={name}
            />
          </>
        )
        : (
          <>
            <Link href="/">
              <a>
                <img src="/images/profile.jpg" 
                  className={`${styles.headerHomeImage} ${utilStyles.borderCircle}`} 
                  alt={name}
                />
              </a>
            </Link>
            <h2 className={utilStyles.headingLg}>
              <Link href="/">
                <a className={utilStyles.colorInherit}>{name}</a>
              </Link>
            </h2>
          </>
        )
        }
      </header>
      {children}
    </div>
  );
}
